import React from "react";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

class AlertDialog extends React.Component {
  state = {
    open: false
  };

  handleClickOpen = () => {
    if(this.props.playerName !== '') this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
    this.props.parentCallback('');
  };

  // ensure user has enough budget for the player. which means passing the budget here
  //0. get current player price. this.props.playerName = key
  //1. if not enough budget, do nothing. else do this.props.addPlayerCallback
  //2. outside if-else, this.handleClose()
  handleAgree = () => {
    let playerPrice = this.props.playerPrice;
    if(this.props.playerPrice <= this.props.budget){
        this.props.addPlayerCallback(this.props.playerName);
    }

    this.handleClose();
  };
  handleDisagree = () => {
    this.handleClose();
  };
  render() {
    return (
      <div>
        {/* Button to trigger the opening of the dialog */}
        <Button onClick={this.handleClickOpen}>Add Player</Button>
        {/* Dialog that is displayed if the state open is true */}
        <Dialog
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {`Add Player?`}
          </DialogTitle>
          {/* <DialogContent>
            <DialogContentText id="alert-dialog-description">
                {""}
              You are successful in life!
            </DialogContentText>
          </DialogContent> */}
          <DialogActions>
            <Button onClick={this.handleDisagree} color="primary">
              No
            </Button>
            <Button onClick={this.handleAgree} color="primary" autoFocus>
              Yes
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}

export default AlertDialog;
